router.put("/:userId", async (req, res, next) => {
   const { type } = req.query;
   const { userId } = req.params;
   console.log("SOY TYPE", type, "SOY USERID", userId);
   const user = await User.findOne({ where: { id: userId } });
   console.log(userId);
   try {
      switch (type) {
         case "admin":
            if (user.isAdmin) {
               user.isAdmin = false;
               await user.save();
               res.send(`the user ${user.name} is no longer an administrator`);
            } else {
               user.isAdmin = true;
               await user.save();
               res.send(`the user ${user.name} is now an administrator`);
            }
            break;
         case "profile":
            const { name, lastName, newAddress, newPhone } = req.body;
            console.log(
               "SOY API PROFILE",
               name,
               lastName,
               newAddress,
               newPhone
            );
            user.name = name;
            user.lastName = lastName;
            user.address = newAddress;
            user.phone = newPhone;
            await user.save();
            res.send(`Changed successfully`);
            break;
         case "image":
            const { newImage } = req.body;
            user.image = newImage;
            await user.save();
            res.send(`image changed successfully`);
         case "password":
            const { oldPassword, newPassword } = req.body;
            console.log(
               "SOY DEL BACK Y TRAIGO OLD",
               oldPassword,
               "NEW",
               newPassword
            );
            if (bcryptjs.compareSync(oldPassword, user.password)) {
               let passwordHash = await bcryptjs.hash(newPassword, 8);
               user.password = passwordHash;
               await user.save();
               res.send(`password changed successfully`);
            } else {
               res.send(`Password is incorrect`);
            }
         case "ban":
            if (user.isBaned) {
               user.isBaned = false;
               await user.save();
               res.send(`the user ${user.name} has been unbanned`);
            } else {
               user.isBaned = true;
               await user.save();
               res.send(`the user ${user.name} has been banned`);
            }
            break;
         default:
            break;
      }
   } catch (err) {
      next(err);
   }
});

